#include<stdio.h>#include<string.h>#include<malloc.h>char** delete1(const char** num, int numSize){	int i = 0;	int j = 0;	const char** s1 = (char**)malloc(sizeof(char*) * numSize);	int u = 0;	while (i < numSize)	{		j = 0;		while (j < numSize)		{			if (strcmp(num[i], num[j]) == 0 && i != j)  //判断两个字符串是否相等，相等为零，不相等不为0，strcmp函数的作用			{				break;			}			j++;		}		if (j == numSize)		{			s1[u++] = &num[i][0]; //每一个指针指向一个首地址		}		i++;	}	while (u < numSize)	{		s1[u++] = NULL; //指针赋空	}	int mm = 0;	while (mm < u)	{		printf("%s\n", s1[mm]); //输出字符串的内容		mm++;	}	return (char**)s1;}int countWords(const char** words1, int words1Size, char** words2, int words2Size){	//char *(l1[3])=delete1(words1,words1Size);	words1 = delete1(words1, words1Size);	words2 = delete1(words2, words2Size);	int i, j;	i = 0;	j = 0;	printf("%d\n", i);	while (words1[i])	{		i++;	}	while (words2[j])	{		j++;	}	int counter = 0;	int m, n;	m = 0;	n = 0;	while (m < i)	{		n = 0;		while (n < j)		{			if (!strcmp(words1[m], words2[n]))			{				counter++;				break;			}			n++;		}		m++;	}	return counter;}int main(){	const char** s1 = (char**)malloc(sizeof(char*) * 5);	s1[0] = "leetcode";	s1[1] = "is";	s1[2] = "amazing";	s1[3] = "as";	s1[4] = "is";	char** s2 = (char**)malloc(sizeof(char*) * 3);	s1[0] = "amazing";	s1[1] = "leetcode";	s1[2] = "is";	printf("%d", countWords(s1, 5, s2, 3));	return 0;}